	public function getcre8MenuItems($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(cre8ContentPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collcre8MenuItems === null) {
			if ($this->isNew()) {
			   $this->collcre8MenuItems = array();
			} else {

				$criteria->add(cre8MenuItemPeer::CONTENT_ID, $this->id);

				cre8MenuItemPeer::addSelectColumns($criteria);
				$this->collcre8MenuItems = cre8MenuItemPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(cre8MenuItemPeer::CONTENT_ID, $this->id);

				cre8MenuItemPeer::addSelectColumns($criteria);
				if (!isset($this->lastcre8MenuItemCriteria) || !$this->lastcre8MenuItemCriteria->equals($criteria)) {
					$this->collcre8MenuItems = cre8MenuItemPeer::doSelect($criteria, $con);
