	/**
	 * Move current node and its children to location $destLeft and updates rest of tree
	 *
	 * @param      int	$destLeft Destination left value
	 * @param      int	$levelDelta Delta to add to the levels
	 * @param      PropelPDO $con		Connection to use.
	 */
	protected function moveSubtreeTo($destLeft, $levelDelta, PropelPDO $con = null)
	{
		$left  = $this->getLeftValue();
		$right = $this->getRightValue();
	
		$treeSize = $right - $left +1;
		
		if ($con === null) {
			$con = Propel::getConnection(cre8MenuItemPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
			
		$con->beginTransaction();
		try {
			// make room next to the target for the subtree
			cre8MenuItemPeer::shiftRLValues($treeSize, $destLeft, null, $con);
		
			if ($left >= $destLeft) { // src was shifted too?
				$left += $treeSize;
				$right += $treeSize;
			}
			
			if ($levelDelta) {
				// update the levels of the subtree
				cre8MenuItemPeer::shiftLevel($levelDelta, $left, $right, $con);
			}
			
			// move the subtree to the target
			cre8MenuItemPeer::shiftRLValues($destLeft - $left, $left, $right, $con);
		
			// remove the empty room at the previous location of the subtree
			cre8MenuItemPeer::shiftRLValues(-$treeSize, $right + 1, null, $con);
			
			// update all loaded nodes
			cre8MenuItemPeer::updateLoadedNodes($con);
			
			$con->commit();
		} catch (PropelException $e) {
			$con->rollback();
			throw $e;
		}
	}
	
	/**
	 * Deletes all descendants for the given node
	 * Instance pooling is wiped out by this command, 
	 * so existing cre8MenuItem instances are probably invalid (except for the current one)
	 *
	 * @param      PropelPDO $con Connection to use.
	 *
	 * @return     int 		number of deleted nodes
	 */
	public function deleteDescendants(PropelPDO $con = null)
	{
		if($this->isLeaf()) {
			// save one query
			return;
		}
		if ($con === null) {
			$con = Propel::getConnection(cre8MenuItemPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$left = $this->getLeftValue();
		$right = $this->getRightValue();
		$criteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$criteria->add(cre8MenuItemPeer::LEFT_COL, $left, Criteria::GREATER_THAN);
		$criteria->add(cre8MenuItemPeer::RIGHT_COL, $right, Criteria::LESS_THAN);
		$con->beginTransaction();
		try {
			// delete descendant nodes (will empty the instance pool)
			$ret = cre8MenuItemPeer::doDelete($criteria, $con);
			
			// fill up the room that was used by descendants
			cre8MenuItemPeer::shiftRLValues($left - $right + 1, $right, null, $con);
			
			// fix the right value for the current node, which is now a leaf
			$this->setRightValue($left + 1);
			
			$con->commit();
		} catch (Exception $e) {
			$con->rollback();
			throw $e;
		}
		
		return $ret;
	}
	
