	/**
	 * Adds $delta to all L and R values that are >= $first and <= $last.
	 * '$delta' can also be negative.
	 *
	 * @param      int $delta		Value to be shifted by, can be negative
	 * @param      int $first		First node to be shifted
	 * @param      int $last			Last node to be shifted (optional)
	 * @param      PropelPDO $con		Connection to use.
	 */
	public static function shiftRLValues($delta, $first, $last = null, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(cre8MenuItemPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
	
		// Shift left column values
		$whereCriteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$criterion = $whereCriteria->getNewCriterion(cre8MenuItemPeer::LEFT_COL, $first, Criteria::GREATER_EQUAL);
		if (null !== $last) {
			$criterion->addAnd($whereCriteria->getNewCriterion(cre8MenuItemPeer::LEFT_COL, $last, Criteria::LESS_EQUAL));
		}
		$whereCriteria->add($criterion);
		
		$valuesCriteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$valuesCriteria->add(cre8MenuItemPeer::LEFT_COL, array('raw' => cre8MenuItemPeer::LEFT_COL . ' + ?', 'value' => $delta), Criteria::CUSTOM_EQUAL);
	
		BasePeer::doUpdate($whereCriteria, $valuesCriteria, $con);
	
		// Shift right column values
		$whereCriteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$criterion = $whereCriteria->getNewCriterion(cre8MenuItemPeer::RIGHT_COL, $first, Criteria::GREATER_EQUAL);
		if (null !== $last) {
			$criterion->addAnd($whereCriteria->getNewCriterion(cre8MenuItemPeer::RIGHT_COL, $last, Criteria::LESS_EQUAL));
		}
		$whereCriteria->add($criterion);
	
		$valuesCriteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$valuesCriteria->add(cre8MenuItemPeer::RIGHT_COL, array('raw' => cre8MenuItemPeer::RIGHT_COL . ' + ?', 'value' => $delta), Criteria::CUSTOM_EQUAL);
	
		BasePeer::doUpdate($whereCriteria, $valuesCriteria, $con);
	}
	
	/**
	 * Adds $delta to level for nodes having left value >= $first and right value <= $last.
	 * '$delta' can also be negative.
	 *
	 * @param      int $delta		Value to be shifted by, can be negative
	 * @param      int $first		First node to be shifted
	 * @param      int $last			Last node to be shifted
	 * @param      PropelPDO $con		Connection to use.
	 */
	public static function shiftLevel($delta, $first, $last, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(cre8MenuItemPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
	
		$whereCriteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$whereCriteria->add(cre8MenuItemPeer::LEFT_COL, $first, Criteria::GREATER_EQUAL);
		$whereCriteria->add(cre8MenuItemPeer::RIGHT_COL, $last, Criteria::LESS_EQUAL);
		
		$valuesCriteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
		$valuesCriteria->add(cre8MenuItemPeer::LEVEL_COL, array('raw' => cre8MenuItemPeer::LEVEL_COL . ' + ?', 'value' => $delta), Criteria::CUSTOM_EQUAL);
	
		BasePeer::doUpdate($whereCriteria, $valuesCriteria, $con);
	}
	
	/**
	 * Reload all already loaded nodes to sync them with updated db
	 *
	 * @param      PropelPDO $con		Connection to use.
	 */
	public static function updateLoadedNodes(PropelPDO $con = null)
	{
		if (Propel::isInstancePoolingEnabled()) {
			$keys = array();
			foreach (cre8MenuItemPeer::$instances as $obj) {
				$keys[] = $obj->getPrimaryKey();
			}
	
			if (!empty($keys)) {
				// We don't need to alter the object instance pool; we're just modifying these ones
				// already in the pool.
				$criteria = new Criteria(cre8MenuItemPeer::DATABASE_NAME);
				$criteria->add(cre8MenuItemPeer::ID, $keys, Criteria::IN);
	
				$stmt = cre8MenuItemPeer::doSelectStmt($criteria, $con);
				while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
					$key = cre8MenuItemPeer::getPrimaryKeyHashFromRow($row, 0);
					if (null !== ($object = cre8MenuItemPeer::getInstanceFromPool($key))) {
						$object->setLeftValue($row[1]);
						$object->setRightValue($row[2]);
						$object->setLevel($row[3]);
					}
				}
				$stmt->closeCursor();
			}
		}
	}
	
	/**
	 * Update the tree to allow insertion of a leaf at the specified position
	 *
	 * @param      int $left	left column value
	 * @param      PropelPDO $con	Connection to use.
	 */
	public static function makeRoomForLeaf($left, PropelPDO $con = null)
	{	
		// Update database nodes
		cre8MenuItemPeer::shiftRLValues(2, $left, null, $con);
	
		// Update all loaded nodes
		cre8MenuItemPeer::updateLoadedNodes($con);
	}
	
	/**
	 * Update the tree to allow insertion of a leaf at the specified position
	 *
	 * @param      PropelPDO $con	Connection to use.
	 */
	public static function fixLevels(PropelPDO $con = null)
	{
		$c = new Criteria();
		$c->addAscendingOrderByColumn(cre8MenuItemPeer::LEFT_COL);
		$stmt = cre8MenuItemPeer::doSelectStmt($c, $con);
		
		// set the class once to avoid overhead in the loop
		$cls = cre8MenuItemPeer::getOMClass(false);
		$level = null;
		// iterate over the statement
		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
	
			// hydrate object
			$key = cre8MenuItemPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null === ($obj = cre8MenuItemPeer::getInstanceFromPool($key))) {
				$obj = new $cls();
				$obj->hydrate($row);
				cre8MenuItemPeer::addInstanceToPool($obj, $key);
			}
			
			// compute level
			// Algorithm shamelessly stolen from sfPropelActAsNestedSetBehaviorPlugin
			// Probably authored by Tristan Rivoallan
			if ($level === null) {
				$level = 0;
				$i = 0;
				$prev = array($obj->getRightValue());
			} else {
				while ($obj->getRightValue() > $prev[$i]) {
					$i--;
				}
				$level = ++$i;
				$prev[$i] = $obj->getRightValue();
			}
			
			// update level in node if necessary
			if ($obj->getLevel() !== $level) {
				$obj->setLevel($level);
				$obj->save($con);
			}
		}
		$stmt->closeCursor();
	}

