	/**
	 * Soft delete records, given a cre8MenuItem or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or cre8MenuItem object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		          rethrown wrapped into a PropelException.
	 */
	public static function doSoftDelete($values, PropelPDO $con = null)
	{
		if ($values instanceof Criteria) {
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof cre8MenuItem) {
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key
			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(cre8MenuItemPeer::ID, (array) $values, Criteria::IN);
		}
		$criteria->add(cre8MenuItemPeer::DELETED_AT, time());
		return cre8MenuItemPeer::doUpdate($criteria, $con);
	}
	
	/**
	 * Delete or soft delete records, depending on cre8MenuItemPeer::$softDelete
	 *
	 * @param      mixed $values Criteria or cre8MenuItem object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
