		}
		$selectCriteria = new Criteria();
		$selectCriteria->add(cre8MenuItemPeer::DELETED_AT, null, Criteria::ISNULL);
		$selectCriteria->setDbName(cre8MenuItemPeer::DATABASE_NAME);
		$modifyCriteria = new Criteria();
